import React, { useEffect, useRef, useState } from 'react';
import Slide from './Slide';

const loadSkulpt = (callback) => {
  // Create and append Skulpt script
  const script1 = document.createElement('script');
  script1.src = 'https://cdnjs.cloudflare.com/ajax/libs/skulpt/1.1.0/skulpt.min.js';
  script1.onload = () => {
    const script2 = document.createElement('script');
    script2.src = 'https://cdnjs.cloudflare.com/ajax/libs/skulpt/1.1.0/skulpt-stdlib.js';
    script2.onload = () => callback();
    document.body.appendChild(script2);
  };
  document.body.appendChild(script1);
};

const runPython = (code, outputRef) => {
  if (!window.Sk) {
    console.error('Skulpt is not loaded.');
    return;
  }

  // Configure Skulpt
  window.Sk.configure({
    output: text => {
      outputRef.current.innerText += text; // Append to the output element
      outputRef.current.scrollTop = outputRef.current.scrollHeight; // Auto-scroll to the bottom
    },
    read: file => {
      if (window.Sk.builtinFiles === undefined || window.Sk.builtinFiles["files"][file] === undefined) {
        throw new Error(`File not found: '${file}'`);
      }
      return window.Sk.builtinFiles["files"][file];
    }
  });

  // Initialize TurtleGraphics if needed
  (window.Sk.TurtleGraphics || (window.Sk.TurtleGraphics = {})).target = 'mycanvas';

  // Run Python code
  const promise = window.Sk.misceval.asyncToPromise(() => window.Sk.importMainWithBody("<stdin>", false, code, true));
  promise.then(() => console.log("success")).catch(err => {
    console.error(err.toString());
    outputRef.current.innerText += `Error: ${err.toString()}`; // Display error in output
  });
};

const PythonCompilerSlide = () => {
  const outputRef = useRef();
  const [skulptLoaded, setSkulptLoaded] = useState(false);

  useEffect(() => {
    loadSkulpt(() => {
      console.log('Skulpt loaded');
      setSkulptLoaded(true);
    });
  }, []);

  const handleRunClick = () => {
    if (!skulptLoaded) {
      console.error('Skulpt is not loaded yet.');
      return;
    }

    const code = document.getElementById("python-code").value;
    console.log(code, 'code');

    outputRef.current.innerText = ''; // Clear previous output
    runPython(code, outputRef);
  };

  return (
    <Slide className="python-compiler-slide">
      <textarea id="python-code" defaultValue="print('Hello, World!')" />
      <button onClick={handleRunClick} disabled={!skulptLoaded}>Run Python Code</button>
      <pre ref={outputRef}></pre>
      <canvas id="mycanvas"></canvas>
    </Slide>
  );
};

export default PythonCompilerSlide;
