import React, { useState } from "react";
import Header from "./components/Header";
import Footer from "./components/Footer";
import OpeningSlide from "./slides/OpeningSlide";
import ImageSlide from "./slides/ImageSlide";
import ChartSlide from "./slides/ChartSlide";
import CodeBlockSlide from "./slides/CodeBlockSlide";
// import PythonCompilerSlide from "./slides/PythonCompilerSlide";
// import ClosingSlide from "./slides/ClosingSlide";
import "./App.css";

const slides = [
  <OpeningSlide />,
  <ImageSlide />,
  <ChartSlide />,
  <CodeBlockSlide />,
  // <PythonCompilerSlide />,
  // <ClosingSlide />,
];

const App = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [isDarkMode, setIsDarkMode] = useState(false);

  const nextSlide = () => {
    if (currentSlide < slides.length - 1) setCurrentSlide(currentSlide + 1);
  };

  const prevSlide = () => {
    if (currentSlide > 0) setCurrentSlide(currentSlide - 1);
  };

  const toggleTheme = () => setIsDarkMode(!isDarkMode);

  return (
    <div className={`App ${isDarkMode ? "dark" : "light"}`}>
      <Header toggleTheme={toggleTheme} />
      <div className="slide-container">{slides[currentSlide]}</div>
      <Footer />
      <button onClick={prevSlide} disabled={currentSlide === 0}>
        Previous
      </button>
      <button onClick={nextSlide} disabled={currentSlide === slides.length - 1}>
        Next
      </button>
    </div>
  );
};

export default App;
